package utube_javabro;

public class Stage3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//Swap two variables
		//swapping x and y, using another variable called temp
		//unless the prog language has a direct method to swap
		
		int x = 10;
		int y = 20;
		int temp = 0;
		
		temp = y;
		y = x;
		x = temp;
		
		System.out.println(x);
		System.out.println(y);

		
	}

}
